import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Register from './components/Register';
import Login from './components/Login';
import VerifyEmail from './components/VerifyEmail';
import ForgotPassword from './components/ForgotPassword';
import ResetPassword from './components/ResetPassword';
import Profile from './components/Profile';
import Dashboard from './components/Dashboard';
import Sidebar from './components/Sidebar';
import QuestionBank from './components/QuestionBank';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#ff4081', // Pink
    },
    secondary: {
      main: '#7c4dff', // Purple
    },
    background: {
      default: '#f3e5f5', // Light purple
      paper: '#fff8e1', // Light yellow
    },
    success: {
      main: '#00e676', // Green
    },
    error: {
      main: '#ff1744', // Red
    },
    info: {
      main: '#2979ff', // Blue
    },
    warning: {
      main: '#ffc400', // Yellow
    },
  },
  typography: {
    fontFamily: 'Comic Sans MS, Comic Sans, cursive',
    h5: {
      fontWeight: 700,
      color: '#7c4dff',
      letterSpacing: 2,
    },
    h4: {
      fontWeight: 900,
      color: '#ff4081',
      letterSpacing: 3,
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 30,
          fontWeight: 700,
          textTransform: 'uppercase',
          boxShadow: '0 4px 20px 0 rgba(255,64,129,0.2)',
        },
      },
    },
    MuiContainer: {
      styleOverrides: {
        root: {
          borderRadius: 20,
          boxShadow: '0 8px 40px 0 rgba(124,77,255,0.15)',
          background: 'linear-gradient(135deg, #fff8e1 0%, #f3e5f5 100%)',
        },
      },
    },
  },
});

function App() {
  const [route, setRoute] = React.useState(window.location.pathname);
  React.useEffect(() => {
    const onPopState = () => setRoute(window.location.pathname);
    window.addEventListener('popstate', onPopState);
    return () => window.removeEventListener('popstate', onPopState);
  }, []);
  const handleNavigate = (path) => {
    window.history.pushState({}, '', path);
    setRoute(path);
  };
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ display: 'flex' }}>
        <Sidebar onNavigate={handleNavigate} selected={route} />
        <Box sx={{ flex: 1, minHeight: '100vh', background: theme.palette.background.default }}>
          <Routes>
            <Route path="/auth/register" element={<Register />} />
            <Route path="/auth/login" element={<Login />} />
            <Route path="/auth/verify-email" element={<VerifyEmail />} />
            <Route path="/auth/forgot-password" element={<ForgotPassword />} />
            <Route path="/auth/reset-password" element={<ResetPassword />} />
            <Route path="/users/profile" element={<Profile />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/question-bank" element={<QuestionBank />} />
            <Route path="/" element={<Navigate to="/auth/login" />} />
          </Routes>
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default App;
